mapper.update:
update cmfz_user
phoneNum=#{phoneNum,jdbcType=VARCHAR}, username=#{userName}, password=#{password}, salt=#{salt}, dharmaName=#{dharmaName,} province=#{province}, city=#{city}, sex=#{sex}, headPic=#{headPic}, status=#{status}, date=UNIX_TIMESTAMP(#{phoneNum}), regDate=UNIX_TIMESTAMP(#{regDate}), masterId=#{masterId}
where id=#{id}
